package kata

import "testing"

func TestCalcTimeUnit2(t *testing.T) {
	want := 2
	result := CalcTimeUnit("1100110011001100000011000000111111001100111111001111110000000000000011001111110011111100111111000000110011001111110000001111110011001100000011")
	if want != result {
		t.Fatalf("Expected: %d Got: %d", want, result)
	}
}

